        private string ProduceHolmesList(string targetImdbName)
        {
            var stringOut = "";

            // get holmes list
            var holmeses = (from a in Db.Actors
                            where a.IMDbName != null
                            select new ToyHolmes { ID = a.ID, Name = a.IMDbName }).ToList();

            // calculate numbers
            for (int i = 0; i < holmeses.Count(); i++)
            {
                var holmesTesting = holmeses[i];
                BaconNumber baconNum = new BaconNumber(holmesTesting.ID, holmesTesting.Name, targetImdbName);

                stringOut += "; Holmes " + holmesTesting.Name + " distance " + baconNum.Number;
            }

            return stringOut;
        }

        public ViewResult Scraps()
        {
            // How about an expansion of the HolmesNum to generate a network?
            // Inspired by talking to Ian Rennie about connections between Holmeses
            // 'here's one for you: Jeremy Brett was in Mad Dogs And Englishmen with C Thomas Howell, who was in the show Smith with JLM.'

            // Colour code the links for closeness, rather than showing the intermediate non-Holmes actors
            // Make it so you can click on one to see the details
            // I'm envisaging something like the navigation map on my tablet for Elite Dangerous star charts
            // The links are the important thing, not the layout. Is there a library to make them fit on the page / bounce around until they fit?

            // Answer: yes there is, check out d3.js http://bl.ocks.org/mbostock/4062045

            // This is going to be computation heavy, so store the links in a new database table and have a recalculate action you can call on demand
            // e.g. when you've added someone new
            // Initially might need to make a few links by hand and work on the display / on the network drawing
            // then we can think about using the Bacon code to generate links

            // Perhaps two views: closeness of net, and chronological...ness, which means we'll need dates on the links
            // They'll be similar but not identical: actors have to both be alive, to appear together
            // (unless they are Basil Rathbone who managed to be in TGMD anyway)
            // Basically we're looking to chase back the lineage of the character into the very early days of film

            string[] blockedNames = { "Nobody" };

            // get holmes list
            var holmeses = (from a in Db.Actors
                            where a.IMDbName != null
                            select new { 
                                a.ID,
                                a.IMDbName,
                                FirstOrderLinks = a.Appearances
                                .Select(app => app.Episode)
                                .SelectMany(e => e.Appearances)
                                .Where(app => app.Actor.IMDbName != null && app.ActorID != a.ID)
                                .Select(app => app.ActorID)
                            }).ToList();

            var nodeList = new List<jsonnode>();
            var linkList = new List<jsonlink>();

            // calculate numbers
            for (int i = 0; i < holmeses.Count(); i++)
            {
                var rootHolmes = holmeses[i];

                if (blockedNames.Contains(rootHolmes.IMDbName))
                {
                    continue;
                }

                for (int j = i + 1; j < holmeses.Count(); j++)
                {
                    var leafHolmes = holmeses[j];
                    var leafJoin = 0;

                    if (rootHolmes.FirstOrderLinks.Contains(leafHolmes.ID))
                    {
                        leafJoin = 1;
                    }

                    // BaconNumber baconNum = new BaconNumber(rootHolmes.ID, rootHolmes.IMDbName, leafHolmes.IMDbName);

                    //if (!blockedNames.Contains(leafHolmes.IMDbName) && baconNum.Number > 0)
                    //{
                    //    int linkStrength;

                    //    if (baconNum.Number > 0)
                    //    {
                    //        linkStrength = Math.Min(25 / (baconNum.Number * baconNum.Number), 1);
                    //    }
                    //    else
                    //    {
                    //        linkStrength = 1;
                    //    }

                    //    linkList.Add("{\"source\": \"" + rootHolmes.ID + "\", \"target\": \"" + leafHolmes.ID + "\", \"value\": " + linkStrength + "}");
                    //}

                    if (leafJoin > 0)
                    {
                        linkList.Add(new jsonlink
                        {
                            source = rootHolmes.ID,
                            target = leafHolmes.ID,
                            value = leafJoin
                        });

                        if (!nodeList.Where(h => h.id == rootHolmes.ID).Any())
                        {
                            nodeList.Add(new jsonnode
                            {
                                id = rootHolmes.ID,
                                name = rootHolmes.IMDbName.Replace("'", "").ToUpper(),
                                group = 1
                            });
                        }

                        if (!nodeList.Where(h => h.id == leafHolmes.ID).Any())
                        {
                            nodeList.Add(new jsonnode
                            {
                                id = leafHolmes.ID,
                                name = leafHolmes.IMDbName.Replace("'", "").ToUpper(),
                                group = 1
                            });
                        }
                    }
                }
            }

            var jsonString = "{ \"nodes\" : " + JsonConvert.SerializeObject(nodeList) + ", \"links\" : " + JsonConvert.SerializeObject(linkList) + " }";

            //var jsonString = StoredScrapsJson();

            //jsonnodelinklist jsonList = JsonConvert.DeserializeObject<jsonnodelinklist>(jsonString);
            //jsonList.links.RemoveAll(l => l.value > 2 || l.value < 0);
            //jsonList.links.ForEach(l => l.value = (int)Math.Max(Math.Floor((double)30 / (l.value * l.value)), 1));

            //jsonString = JsonConvert.SerializeObject(jsonList);

            var model = new ScrapsView { HolmesLinks = jsonString };

            return View(model);
        }

        protected class jsonnodelinklist
        {
            public List<jsonnode> nodes;

            public List<jsonlink> links;
        }
        protected class jsonnode
        {
            public int id;
            public string name;
            public int group;
        }
        protected class jsonlink
        {
            public int source;
            public int target;
            public int value;
        }

